<input type="radio" name="<?php echo $el->name; ?>" value="<?php echo $el->display->value; ?>"<?php echo isset($el->element->class) ? ' class="' . $el->element->class . '"': '' ; ?> id="<?php echo isset($el->display->id) ? $el->display->id : '_' . $el->name ; ?>"<?php echo $el->checked || $el->display->value == $el->value ? ' checked="checked"' : '' ;?> /><!-- bootstrap.form.radio -->